-- ----------------------------
-- Table structure for feedback
-- ----------------------------
DROP TABLE IF EXISTS `feedback`;
CREATE TABLE `feedback` (
  `id` int(11) NOT NULL auto_increment COMMENT '反馈id ',
  `userId` int(11) NOT NULL COMMENT '该条反馈信息的用户id',
  `content` varchar(255) NOT NULL COMMENT '反馈的内容',
  `time` datetime NOT NULL COMMENT '反馈的时间',
  `state` tinyint(2) NOT NULL default '1' COMMENT '反馈信息的状态 1为还没处理改条反馈信息 0为已经处理了该条反馈信息',
  PRIMARY KEY  (`id`),
  KEY `fp1` (`userId`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

